{ LS TEST START ------------------------------------- }
前日收盘价:= REF(C,2);
昨日收盘价:= REF(C,1);
昨日跌幅A:= (昨日收盘价-前日收盘价)/前日收盘价;
昨日跌幅:= CON2STR( ROUND2(昨日跌幅A * 100, 2), 2);
DRAWNUMBER_FIX(1,0,0,0, FROMOPEN);
DRAWNUMBER_FIX(1,0,0.1,0, REF(C,1));
{
DRAWNUMBER_FIX(1,0.2,0,0, DAYSTOTODAY);
 DRAWTEXT(DAYSTOTODAY=0, H+0.9, 字符C),COLORMAGENTA;
