{ 基本单位 START ------------------------------------- }
百万:= 100 * 10000;
千万:= 1000 * 10000;
亿:= 10000 * 10000;
{ 基本单位 END --------------------------------------- }

{ 基本参数 START ------------------------------------- }
量比:= DYNAINFO(17);
流通市值:= FINANCE(40);
前收盘价:= DYNAINFO(3);
昨收盘价:= 前收盘价;
昨日额:= REF(AMO,1);
竞价额:= DYNAINFO(15);
开盘价:= DYNAINFO(4);

昨竞比:= 竞价额/昨日额;
高开:= (开盘价 - 昨收盘价) / 昨收盘价; 

涨跌幅1:= (C-REF(C,1))/REF(C,1);
昨日涨跌幅:= IF(CURRBARSCOUNT=1, REF(涨跌幅1, 1), 0);

{ 基本参数 END --------------------------------------- }


{ 条件辅助 START ------------------------------------- }
NOT_IN_封板:= FINDSTR(ZDBLOCK, 'X封板') = 0;
IN_封板:= FINDSTR(ZDBLOCK, 'X封板');
IN_OC:= INBLOCK('自选股') OR FINDSTR(ZDBLOCK, 'O龙头') OR FINDSTR(ZDBLOCK, 'OC');
IN_OSZ:= INBLOCK('自选股') OR FINDSTR(ZDBLOCK, 'O') OR FINDSTR(ZDBLOCK, 'S') OR FINDSTR(ZDBLOCK, 'Z');
IN_OSZ_NOT封板:= NOT_IN_封板 AND IN_OSZ;
IN_OC_O_NOT封板:= IN_OC OR NOT_IN_封板;
IN_OC_封板:= IN_封板 AND IN_OC;
IN_自选:= IN_OSZ_NOT封板 OR IN_OC_封板;

IN_竞价强:= FINDSTR(ZDBLOCK, '竞价');
IN_XJ2:= FINDSTR(ZDBLOCK, 'XJ2');
IN_XJ3:= FINDSTR(ZDBLOCK, 'XJ3');
IS_XJ:= IN_竞价强 OR IN_XJ2 OR IN_XJ3;

IS大跌:= 昨日涨跌幅 < -5/100;
IS小跌:= 昨日涨跌幅 < -2/100 AND 昨日涨跌幅 > -5/100;
{ 条件辅助 END --------------------------------------- }


{ 基本选股条件 START --------------------------------- }
价格条件:= 前收盘价 > 3;
流通市值条件:= 流通市值 < 3000 * 亿;

基本选股条件:= 价格条件 AND 流通市值条件;
{ 基本选股条件 END ----------------------------------- }


{ 通用竞价强：值得关注 }
IS_通用竞价强:= NOT_IN_封板 AND 高开 > 1/100 AND 竞价额 > 1 * 千万 AND 昨竞比 > 1/100;


{ 打板竞价: 高开大于1%; 竞价额大于2千万; }
IS_打板竞价:= IN_OC_封板 AND 高开 > 1/100 AND 竞价额 > 1 * 千万;

{ T字首板: 加速; 抢筹; 最强; 一致; }
IS_T字板:= IN_OC_O_NOT封板 AND 高开 > 9/100 AND 竞价额 > 1 * 千万 AND 昨竞比 > 3/100;


{ 放量异动: }
放量异动1:= NOT_IN_封板 AND 高开 > 0/100 AND 竞价额 > 3 * 千万 AND 昨竞比 > 1/100;
放量异动2:= NOT_IN_封板 AND 高开 > 1/100 AND 竞价额 > 1 * 千万 AND 昨竞比 > 2/100;
IS_放量异动:= 放量异动1 OR 放量异动2;


{ 自选异动: }
自选异动1:= IN_自选 AND 高开 > 1/100 AND 竞价额 > 5 * 百万 AND 昨竞比 > 2/100;
自选异动2:= IN_自选 AND 高开 > 1/100 AND 竞价额 > 1 * 千万 AND 昨竞比 > 1/100;
IS_自选异动:= 自选异动1 OR 自选异动2;


{ 连续上冲: 强势 }
IN_上冲:= FINDSTR(ZDBLOCK, 'X上冲');
连续上冲1:= IN_上冲 AND 高开 > 1/100 AND 竞价额 > 5 * 百万 AND 昨竞比 > 2/100;
连续上冲2:= IN_上冲 AND 高开 > 1/100 AND 竞价额 > 1 * 千万 AND 昨竞比 > 1/100;
IS_连续上冲:= 连续上冲1 OR 连续上冲2;


{ 烂板不弱: 该弱不弱，看多 }
IN_烂板:= FINDSTR(ZDBLOCK, 'X破板');
烂板不弱1:= IN_烂板 AND 高开 > 1/100 AND 竞价额 > 5 * 百万 AND 昨竞比 > 2/100;
烂板不弱2:= IN_烂板 AND 高开 > 0/100 AND 竞价额 > 1 * 千万 AND 昨竞比 > 1/100;
IS_烂板不弱:= 烂板不弱1 OR 烂板不弱2;


{ 烂板上冲: 反转 }
烂板上冲1:= IN_烂板 AND 高开 > 2/100 AND 竞价额 > 1 * 千万 AND 昨竞比 > 5/100;
烂板上冲2:= IN_烂板 AND 高开 > 3/100 AND 竞价额 > 2 * 千万 AND 昨竞比 > 3/100;
IS_烂板上冲:= 烂板上冲1 OR 烂板上冲2;


{ 小盘起跳: }
小盘起跳1:= NOT_IN_封板 AND 高开 > 3/100 AND 竞价额 > 5 * 百万 AND  昨竞比 > 5/100;
小盘起跳2:= NOT_IN_封板 AND 高开 > 2/100 AND 竞价额 > 7 * 百万 AND  昨竞比 > 3/100;
小盘起跳3:= NOT_IN_封板 AND 高开 > 4/100 AND 竞价额 > 4 * 百万 AND  昨竞比 > 7/100;
IS_小盘起跳:= 小盘起跳1 OR 小盘起跳2 OR 小盘起跳3;


{ 放量起跳: 涨幅大于2%；竞价额大于昨日成交4%； 并且至少大于4000万；}
放量起跳1:= NOT_IN_封板 AND 高开 > 2/100 AND 竞价额 > 1 * 千万 AND 昨竞比 > 3/100;
放量起跳2:= NOT_IN_封板 AND 高开 > 2/100 AND 竞价额 > 2 * 千万 AND 昨竞比 > 2/100;
IS_放量起跳:= 放量起跳1 OR 放量起跳2;



{ 成交额+封单字符串输出 START------------------------------------------------- }

成交额:= DYNAINFO(10);
成交额亿:= STRCAT(CON2STR( ROUND2(成交额/亿, 1), 1), '亿  ');
成交额万:= STRCAT(CON2STR( ROUND2(成交额/10000, 2), 0), '万  ');
成交额字符A:= IF(成交额/100000000 > 1, 成交额亿, 成交额万);
成交额字符:= STRCAT(' 额: ', 成交额字符A);

{ 封单额(元) = 买一量(手) * 100股 * 买一价 }
封单额:= DYNAINFO(58) * 100 *  DYNAINFO(20);
封单额亿:= STRCAT(CON2STR( ROUND2(封单额/亿, 1), 1), '亿  ');
封单额万:= STRCAT(CON2STR( ROUND(封单额/10000), 0), '万 ');
封单额字符A:= IF(封单额/100000000 > 1, 封单额亿, 封单额万);
封单额字符:= STRCAT(' 封: ', 封单额字符A);

DRAWTEXT_FIX(1, 0.86, 0.01, 1, STRCAT(成交额字符, 封单额字符)),COLORYELLOW;
{ 成交额+封单字符串输出 END ------------------------------------------------- }


{ 昨成交额+竞价额+比字符输出 START------------------------------------------- }
昨日成交额:= REF(AMO, 1);
昨日成交额亿:= STRCAT(CON2STR( ROUND2(昨日成交额/亿, 1), 1), '亿  ');
昨日成交额万:= STRCAT(CON2STR( ROUND2(昨日成交额/10000, 2), 0), '万  ');
昨日成交额字符A:= IF(昨日成交额/亿 > 1, 昨日成交额亿, 昨日成交额万);
昨日成交额字符:= STRCAT(' 昨: ', 昨日成交额字符A);


竞价额亿:= STRCAT(CON2STR( ROUND2(竞价额/亿, 1), 1), '亿  ');
竞价额万:= STRCAT(CON2STR( ROUND(竞价额/10000), 0), '万  ');
竞价额字符A:= IF(竞价额/亿 > 1, 竞价额亿, 竞价额万);
竞价额字符:= STRCAT(' 竞: ',竞价额字符A);


昨竞比字符A:= STRCAT(CON2STR(昨竞比 * 100, 1), '% ');
昨竞比字符:= STRCAT(' 比: ', 昨竞比字符A);

昨竞字符A:= STRCAT(昨日成交额字符, 竞价额字符);
昨竞字符:= STRCAT(昨竞字符A, 昨竞比字符);

DRAWTEXT_FIX(1, 0.99, 0.93, 1, 昨竞字符),COLORYELLOW;
{ 昨成交额+竞价额+比字符输出 END------------------------------------------- }


{ 高开字符输出 START------------------------------------------------ }
高开字符:= STRCAT(CON2STR( ROUND2(高开*100, 2), 1), '% ');
DRAWTEXT_FIX(1, 0.84, 0.84, 1, 高开字符),COLORYELLOW;
{ 高开字符输出 END-------------------------------------------------- }


{ 竞价强名称输出 START---------------------------------------------- }

{ IS_打板竞价 OR IS_通用竞价强 OR IS_放量异动 OR IS_自选异动 OR IS_烂板不弱 OR IS_连续上冲 OR IS_小盘起跳}
{ IS_放量起跳 OR IS_烂板上冲 OR IS_T字板 }

IS_爆量:= 竞价额 >  4 * 千万 AND 昨竞比 > 5/100;
IS_蓝筹:= 流通市值 > 200 * 亿;
IS_中盘:= 流通市值 > 40 * 亿 AND 流通市值 < 200 * 亿;
IS_小盘:= 流通市值 < 40 * 亿;
IS_小盘起跳1:= IS_小盘 AND IS_小盘起跳;
IS_蓝筹起跳:= IS_蓝筹 AND IS_放量起跳 AND 高开 > 2/100 AND 竞价额 > 2 * 千万 AND 昨竞比 > 5/100;
IS_反转:= 0;
IS_龙头:= FINDSTR(ZDBLOCK, '龙');
IS_风口:= FINDSTR(ZDBLOCK, 'JF');
IS_特别关注:= IS_爆量 OR IS_蓝筹起跳 OR IS_龙头 OR IS_风口 OR IS_反转;


提示S1:= IF(IS_通用竞价强, '竞价强', '');
提示S2:= IF(IS_打板竞价, '打板竞价', 提示S1);
提示S3:= IF(IS_放量异动, '放量异动', 提示S2);
提示S4:= IF(IS_自选异动, '自选异动', 提示S3);
提示S5:= IF(IS_小盘起跳, '小盘起跳', 提示S4);
提示S6:= IF(IS_放量起跳, '放量起跳', 提示S5);
提示S7:= IF(IS_烂板不弱, '烂板不弱', 提示S6);
提示S8:= IF(IS_连续上冲, '连续上冲', 提示S7);
提示S9:= IF(IS_烂板上冲, '烂板上冲', 提示S8);
提示S10:= IF(IS_T字板, 'T字板', 提示S9);
提示S11:= IF(IS_蓝筹起跳, '蓝筹起跳', 提示S10);


提示SA:= IF(IS_爆量, STRCAT('爆量  ', 提示S11), 提示S11);
提示SB:= IF(IS_反转, STRCAT('反转  ', 提示SA), 提示SA);
提示SC:= IF(IS_风口, STRCAT('风口  ', 提示SB), 提示SB);
提示SD:= IF(IS_龙头, STRCAT('龙头  ', 提示SC), 提示SC);
提示SE:= IF(IS_中盘, STRCAT('中盘  ', 提示SD), 提示SD);
提示SF:= IF(IS_小盘, STRCAT('小盘  ', 提示SE), 提示SE);
提示S:= IF(IS_蓝筹, STRCAT('蓝筹  ', 提示SF), 提示SF);


{ 只有IN竞价强的个股会输出提示字符 }
DRAWTEXT_FIX(1, 0.99, 0.84, 1, 提示S),COLORYELLOW;
DRAWTEXT_FIX(IS_XJ AND IS_特别关注, 0.99, 0.84, 1, 提示S),COLORMAGENTA;

DRAWTEXT_FIX(IS_XJ AND IS_特别关注, 0.98, 0.01, 1, 提示S),COLORMAGENTA;

DRAWTEXT(IS_XJ AND ISLASTBAR AND IS_特别关注, L-0.5, '■■'),COLORGREEN;

{ 竞价强名称输出 END ---------------------------------------------- };


{
DRAWTEXT_FIX(IS_爆量, 0.99, 0.84, 1, 提示S),COLORMAGENTA;
DRAWTEXT_FIX(IS_龙头, 0.99, 0.84, 1, 提示S),COLORMAGENTA;
DRAWTEXT_FIX(IS_蓝筹起跳, 0.99, 0.84, 1, 提示S),COLORMAGENTA;
};

